mapper.select:
    
    SELECT * FROM
		(SELECT ROW_.*,ROWNUM ROWNUM_
		FROM (
		select
		    *
		 from QZ_IESB_FOR_ECIF
	    where 1=1
	    
			AND CLIENT_NAME=#{clientName}
		
		
			AND GLOBAL_ID=#{globalId}
		
			
			) ROW_
		WHERE ROWNUM <= #{limit}*#{page} + #{limit}) 
	WHERE ROWNUM_ > #{limit}*#{page}

	select
	    count(*)
	 from QZ_IESB_FOR_ECIF
    where 1=1
    
		AND CLIENT_NAME=#{clientName}
	
	
		AND GLOBAL_ID=#{globalId}

SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( select bci.ID as id, bci.CHINESE_NAME as chineseName, ecif.GLOBAL_ID as cardId, ecif.GLOBAL_TYPE as cardType, ecif.STATUS as status from BASIC_CUSTOMER_INFORMATION bci left join QZ_IESB_FOR_ECIF ecif on bci.ID = ecif.CUSTOMER_ID where 1=1 and bci.USER_ID = #{userId}
AND bci.CHINESE_NAME=#{chineseName}
AND bci.CARD_ID=#{cardId}
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

	select
	    count(*)
	 from BASIC_CUSTOMER_INFORMATION bci
	 left join QZ_IESB_FOR_ECIF ecif
	 on bci.ID = ecif.CUSTOMER_ID
    where 1=1
    and bci.USER_ID = #{userId}
    
		AND bci.CHINESE_NAME=#{chineseName}
	
	
		AND bci.CARD_ID=#{cardId}
