mapper.select:
    
    SELECT * FROM
		(SELECT ROW_.*,ROWNUM ROWNUM_
		FROM (
		select
		    *
		 from QZ_IESB_FOR_ECIF
	    where 1=1
	    
			AND CLIENT_NAME=#{clientName}
		
		
			AND GLOBAL_ID=#{globalId}
		
		
			AND USER_ID=#{userId}
		
			
			) ROW_
		WHERE ROWNUM <= #{limit}*#{page} + #{limit}) 
	WHERE ROWNUM_ > #{limit}*#{page}

	select
	    count(*)
	 from QZ_IESB_FOR_ECIF
    where 1=1
    
		AND CLIENT_NAME=#{clientName}
	
	
		AND GLOBAL_ID=#{globalId}
	
	
		AND USER_ID=#{userId}

SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( select info.id as id, ecif.customer_id as customerId, ecif.client_name as chineseName, ecif.GLOBAL_ID as cardId, circle.CONTRACT_AMT as applyQuota, info.product_id as productId, info.status as status, info.is_continue as isContinue, n.node_name as nodeName from customer_application_info info left join QZ_IESB_FOR_ECIF ecif on info.customer_id = ecif.CUSTOMER_ID left join customer_application_process p on info.id = p.application_id left join QZ_IESB_FOR_CIRCLE circle on circle.application_id=info.id left join node_audit n on n.id=p.next_node_id where 1=1 and ecif.USER_ID = #{userId}
AND ecif.client_name=#{chineseName}
AND ecif.global_id=#{cardId}
order by info.created_time desc
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( select bci.ID as id, bci.CHINESE_NAME as chineseName, ecif.CLIENT_NO as clientNo, ecif.GLOBAL_ID as cardId, ecif.GLOBAL_TYPE as cardType, bci.DIVISIONAL_STATUS as divisionalStatus from BASIC_CUSTOMER_INFORMATION bci left join QZ_IESB_FOR_ECIF ecif on bci.ID = ecif.CUSTOMER_ID left join customer_application_info i on bci.ID=i.customer_id where 1=1 and bci.USER_ID = #{userId}
AND bci.CHINESE_NAME=#{chineseName}
AND bci.CARD_ID=#{cardId}
AND i.status=#{appStatus}
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

	select
	    count(*)
	 from customer_application_info info 
	 left join QZ_IESB_FOR_ECIF ecif
	 on info.customer_id = ecif.CUSTOMER_ID
    where 1=1
    and ecif.USER_ID = #{userId}
    
		AND ecif.client_name=#{chineseName}
	
	
		AND ecif.global_id=#{cardId}
	
	
		AND info.status=#{appStatus}

SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM (
select ecif.customer_id as id, ecif.client_name as chineseName, ecif.client_no as clientNo, ecif.global_id as cardId, ecif.GLOBAL_TYPE as cardType from qz_iesb_for_ecif ecif inner join (select cont.cus_id from O_CLPM_CTR_LOAN_CONT cont left join o_clpm_acc_loan loan on cont.cus_id=loan.cus_id where cont.prd_id = '100028' and loan.acc_status='1' group by cont.cus_id) con on ecif.client_no = con.cus_id where 1=1 and ecif.customer_id not in (select distinct ecif.customer_id from qz_iesb_for_ecif ecif inner join customer_application_info info on ecif.customer_id =info.customer_id where (info.status='save' or info.status='audit' or info.status='RETURNAPPROVE')) and ecif.USER_ID = #{userId}
AND ecif.client_name=#{chineseName}
AND ecif.global_id=#{cardId}
AND ecif.customer_id in (select customer_id from qz_iesb_for_circle where ret_code = '000000')
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

select count(*) from qz_iesb_for_ecif ecif inner join (select cont.cus_id from O_CLPM_CTR_LOAN_CONT cont left join o_clpm_acc_loan loan on cont.cus_id=loan.cus_id where cont.prd_id = '100028' and loan.acc_status='1' group by cont.cus_id) con on ecif.client_no = con.cus_id where 1=1 and ecif.customer_id not in (select distinct ecif.customer_id from qz_iesb_for_ecif ecif inner join customer_application_info info on ecif.customer_id =info.customer_id where (info.status='save' or info.status='audit' or info.status='RETURNAPPROVE')) and ecif.USER_ID = #{userId}
AND ecif.client_name=#{chineseName}
AND ecif.global_id=#{cardId}
AND ecif.customer_id in (select customer_id from qz_iesb_for_circle where ret_code = '000000')

SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( select ecif.customer_id as id, ecif.client_name as chineseName, ecif.client_no as clientNo, ecif.global_id as cardId, ecif.GLOBAL_TYPE as cardType from qz_iesb_for_ecif ecif where 1 = 1 and ecif.customer_id not in (select distinct ecif.customer_id from qz_iesb_for_ecif ecif inner join customer_application_info info on ecif.customer_id =info.customer_id where (info.status='save' or info.status='audit' or info.status='RETURNAPPROVE')) and ecif.customer_id not in (select ecif.customer_id from qz_iesb_for_ecif ecif inner join (select cont.cus_id from O_CLPM_CTR_LOAN_CONT cont left join o_clpm_acc_loan loan on cont.cus_id=loan.cus_id where cont.prd_id = '100028' and loan.acc_status='1' group by cont.cus_id) con on ecif.client_no =con.cus_id) and ecif.USER_ID = #{userId}
AND ecif.client_name=#{chineseName}
AND ecif.global_id=#{cardId}
AND ecif.customer_id in (select customer_id from qz_iesb_for_circle where ret_code = '000000')
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

select count(*) from qz_iesb_for_ecif ecif where 1 = 1 and ecif.customer_id not in (select distinct ecif.customer_id from qz_iesb_for_ecif ecif inner join customer_application_info info on ecif.customer_id =info.customer_id where (info.status='save' or info.status='audit' or info.status='RETURNAPPROVE')) and ecif.customer_id not in (select ecif.customer_id from qz_iesb_for_ecif ecif inner join (select cont.cus_id from O_CLPM_CTR_LOAN_CONT cont left join o_clpm_acc_loan loan on cont.cus_id=loan.cus_id where cont.prd_id = '100028' and loan.acc_status='1' group by cont.cus_id) con on ecif.client_no =con.cus_id) and ecif.USER_ID = #{userId}
AND ecif.client_name=#{chineseName}
AND ecif.global_id=#{cardId}
AND ecif.customer_id in (select customer_id from qz_iesb_for_circle where ret_code = '000000')
