mapper.select:
SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM (
SELECT cai. ID,cai.customer_id,cai.chinese_name,cai.product_id,cai.card_id,cai.apply_quota,cai.status,cai.Created_Time FROM (SELECT * FROM WF_STATUS_QUEUE_RECORD WHERE EXAMINE_USER = #{userId}) wsqr LEFT JOIN ( SELECT T.ID, T.customer_id, b.chinese_name, T.product_id, b.card_id, circle.CONTRACT_AMT as apply_quota, T.status, T.SERIAL_NUMBER ,T.Created_Time FROM customer_application_info T inner JOIN basic_customer_information b ON T .customer_id = b. ID
LEFT JOIN QZ_IESB_FOR_CIRCLE circle on T.id = circle.application_id ) cai ON wsqr.CURRENT_PROCESS = CAI.SERIAL_NUMBER where 1=1
and cai.chinese_name like '%'||#{chineseName}||'%'
and cai.card_id like '%'||#{cardId}||'%'
and (cai.status=#{status} or cai.status='RETURNAPPROVE')
and cai.status=#{status}
union SELECT T.ID, T.customer_id, b.chinese_name, T.product_id, b.card_id, circle.CONTRACT_AMT as apply_quota, T.status, T.Created_Time FROM customer_application_info T inner JOIN basic_customer_information b ON T .customer_id = b. ID LEFT JOIN QZ_IESB_FOR_CIRCLE circle on T.id = circle.application_id where (b.user_id = #{userId} or b.user_id in ( select user_id from ACCOUNT_MANAGER_PARAMETER where id in (SELECT mbm.child_id FROM manager_belong_map mbm LEFT JOIN account_manager_parameter amp ON amp. ID = mbm.parent_id WHERE amp.user_id = #{userId})))
and b.chinese_name like '%'||#{chineseName}||'%'
and b.card_id like '%'||#{cardId}||'%'
and (T.status=#{status} or T.status='RETURNAPPROVE')
and T.status=#{status}
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}
